   * @param d is the internal data storage to use.
   * @param key is the address to test.
   * @return a boolean.
   */
  function contains(Data storage d, address key)
      internal view returns(bool) {
    return d.values.length == 0
      ? false
      : d.values[d.indices[key]] == key;
  }
}
