pragma solidity ^0.8.4;

import '../interfaces/IHasMembers.sol';
import '../interfaces/IHasGovernors.sol';
import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import '../exchange/ExchangeAccessFacet.sol';
import './FastTokenFacet.sol';
import './lib/AFastFacet.sol';
import './lib/LibFast.sol';
import './lib/LibFastAccess.sol';
import './FastTopFacet.sol';
import './FastFrontendFacet.sol';


/**
* @dev The FAST Access Smart Contract is the source of truth when it comes to
* permissioning and ACLs within a given FAST network.
*/
contract FastAccessFacet is AFastFacet, IHasMembers, IHasGovernors {
  using LibAddressSet for LibAddressSet.Data;
  // Structs.

  /**
   * @dev This structure isn't used anywhere in storage. Instead, it
   * allows various methods of the contract to return all the flags
   * associated with a given address in one go.
   */
  struct Flags {
    bool isGovernor;
    bool isMember;
  }

  // Constants.

  // This represents how much Eth we provision new governors with.
  uint256 constant private GOVERNOR_ETH_PROVISION = 10 ether;
  // This represents how much Eth we provision new members with.
  uint256 constant private MEMBER_ETH_PROVISION = 1 ether;

  // Initializers.

  function initialize(address payable governor)
      external
      diamondInternal {
    // Grab our storage.
    LibFastAccess.Data storage s = LibFastAccess.data();
    // Make sure we havn't initialized yet.
    require(s.version < LibFastAccess.STORAGE_VERSION, 'Already initialized');
    // Initialize access storage.
    s.version = LibFastAccess.STORAGE_VERSION;

    // Add the governor.
    s.governorSet.add(governor, false);
    // Emit!
    emit GovernorAdded(governor);
  }

  // Governorship related stuff.

  /**
   * @dev Queries whether a given address is a governor or not.
   */
  function isGovernor(address candidate)
      external view override returns(bool) {
    return LibFastAccess.data().governorSet.contains(candidate);
  }

  /**
   * @dev Queries the number of governors in the governorship list.
   */
  function governorCount()
      external override view returns(uint256) {
    return LibFastAccess.data().governorSet.values.length;
  }

  /**
   * @dev Returns a page of governors.
   */
  function paginateGovernors(uint256 index, uint256 perPage)
      external override view returns(address[] memory, uint256) {
    return LibPaginate.addresses(LibFastAccess.
      data().governorSet.values,
      index,
      perPage
    );
  }

  /**
   * @dev Adds a governor to the governorship list.
   */
  function addGovernor(address payable governor)
      external override
      spcMembership {
    // Add governor to list.
    LibFastAccess.data().governorSet.add(governor, false);
    // If the address is a regular wallet...
    if (!LibHelpers.isContract(governor)) {
      // Provision the new governor with Eth if possible.
      FastTopFacet(payable(address(this))).payUpTo(governor, GOVERNOR_ETH_PROVISION);
    }
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit IHasGovernors.GovernorAdded(governor);
  }

  /**
   * @dev Removes a governor from the governorship list.
   */
  function removeGovernor(address governor)
      external override
      spcMembership {
    // Remove governor.
    LibFastAccess.data().governorSet.remove(governor, false);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit IHasGovernors.GovernorRemoved(governor);
  }

  /// Membership related stuff.

  /**
   * @dev Queries whether a given address is a member or not.
   */
  function isMember(address candidate)
      external override view returns(bool) {
    return LibFastAccess.data().memberSet.contains(candidate);
  }

  /**
   * @dev Queries the number of members in the membership list.
   */
  function memberCount()
      external override view returns(uint256) {
    return LibFastAccess.data().memberSet.values.length;
  }

  /**
   * @dev Returns a page of members.
   */
  function paginateMembers(uint256 index, uint256 perPage)
      external override view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastAccess.data().memberSet.values,
      index,
      perPage
    );
  }

  /**
   * @dev Adds a member to the membership list.
   */
  function addMember(address payable member)
      external override 
      governance(msg.sender) exchangeMember(member) {
    // Add the member.
    LibFastAccess.data().memberSet.add(member, false);
    // If the address is a regular wallet...
    if (!LibHelpers.isContract(member)) {
      // Provision the new member with Eth if possible.
      FastTopFacet(payable(address(this))).payUpTo(member, MEMBER_ETH_PROVISION);
    }
    // Notify exchange that this member was added to this FAST.
    ExchangeAccessFacet(LibFast.data().exchange).memberAddedToFast(member);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit IHasMembers.MemberAdded(member);
  }

  /**
   * @dev Removes a member from the membership list.
   */
  function removeMember(address member)
      external override 
      governance(msg.sender) {
    // Remove member.
    LibFastAccess.data().memberSet.remove(member, false);
    // Notify exchange that this member was removed from this FAST.
    ExchangeAccessFacet(LibFast.data().exchange).memberRemovedFromFast(member);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit IHasMembers.MemberRemoved(member);
  }

  /// Flags.

  /**
   * @dev Retrieves flags for a given address.
   */
  function flags(address a)
      external view returns(Flags memory) {
    LibFastAccess.Data storage s = LibFastAccess.data();
    return
      Flags({
        isGovernor: s.governorSet.contains(a),
        isMember: s.memberSet.contains(a)
      });
  }
}
","keccak256":"0x4cc8e1a98399f6514da8acf277677313f7f75ef2b21b77eae4238d2a39244d23","license":"MIT"},"contracts/fast/FastFrontendFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastAccess.sol';
import './lib/LibFastToken.sol';


/** @title The SPC Smart Contract.
 *  @dev The SPC contract is the central place for top-level governorship. It requires that a
 *        first member address is passed at construction time.
 */
contract FastFrontendFacet is AFastFacet {
  using LibAddressSet for LibAddressSet.Data;

  // Events.

  // This is an event that is fired whenever any of some of the FAST parameters
  // change, so that the frontend can react to it and refresh the general header
  // for that fast as well as the baseball cards in the FASTs list.
  event DetailsChanged(
    uint256 memberCount,
    uint256 governorCount,
    uint256 totalSupply,
    uint256 transferCredits,
    uint256 reserveBalance,
    uint256 ethBalance
  );

  // Data structures.

  struct Details {
    address addr;
    string name;
    string symbol;
    uint256 decimals;
    uint256 totalSupply;
    uint256 transferCredits;
    bool isSemiPublic;
    bool hasFixedSupply;
    uint256 reserveBalance;
    uint256 memberCount;
    uint256 governorCount;
  }

  struct MemberDetails {
    address addr;
    uint256 balance;
    uint256 ethBalance;
    bool isGovernor;
  }

  // Emitters.

  function emitDetailsChanged()
      external diamondInternal {
    LibFastAccess.Data storage accessData = LibFastAccess.data();
    LibFastToken.Data storage tokenData = LibFastToken.data();
    emit DetailsChanged({
      memberCount: accessData.memberSet.values.length,
      governorCount: accessData.governorSet.values.length,
      totalSupply: tokenData.totalSupply,
      transferCredits: tokenData.transferCredits,
      reserveBalance: tokenData.balances[LibConstants.ZERO_ADDRESS],
      ethBalance: payable(address(this)).balance
    });
  }

  // Public functions.

  function details()
      public view returns(Details memory) {
    LibFast.Data storage topStorage = LibFast.data();
    LibFastAccess.Data storage accessStorage = LibFastAccess.data();
    LibFastToken.Data storage tokenStorage = LibFastToken.data();
    return Details({
      addr: address(this),
      name: tokenStorage.name,
      symbol: tokenStorage.symbol,
      decimals: tokenStorage.decimals,
      totalSupply: tokenStorage.totalSupply,
      transferCredits: tokenStorage.transferCredits,
      isSemiPublic: topStorage.isSemiPublic,
      hasFixedSupply: topStorage.hasFixedSupply,
      reserveBalance: tokenStorage.balances[LibConstants.ZERO_ADDRESS],
      memberCount: accessStorage.memberSet.values.length,
      governorCount: accessStorage.governorSet.values.length
    });
  }

  function detailedMember(address member)
      public view returns(MemberDetails memory) {
    LibFastToken.Data storage tokenStorage = LibFastToken.data();
    LibFastAccess.Data storage accessStorage = LibFastAccess.data();
    return MemberDetails({
      addr: member,
      balance: tokenStorage.balances[member],
      ethBalance: member.balance,
      isGovernor: accessStorage.governorSet.contains(member)
    });
  }

  function paginateDetailedMembers(uint256 index, uint256 perPage)
      external view returns(MemberDetails[] memory, uint256) {
    LibFastAccess.Data storage accessStorage = LibFastAccess.data();
    (address[] memory members, uint256 nextCursor) =
      LibPaginate.addresses(accessStorage.memberSet.values, index, perPage);
    MemberDetails[] memory values = new MemberDetails[](members.length);
    for (uint256 i = 0; i < members.length; ++i) {
      values[i] = detailedMember(members[i]);
    }
    return (values, nextCursor);
  }
}
","keccak256":"0x9bc27617d7c845b32b184f65d2c38ba5a12955e6387152732de8834326a0da4c","license":"MIT"},"contracts/fast/FastHistoryFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastHistory.sol';


contract FastHistoryFacet is AFastFacet {
  /// Minting history-keeping methods.

  function minted(uint256 amount, string calldata ref)
      external diamondInternal() {
    // Keep track of the mint.
    LibFastHistory.data().supplyProofs.push(
      LibFastHistory.SupplyProof({
        op: LibFastHistory.SupplyOp.Mint,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  function burnt(uint256 amount, string calldata ref)
      external diamondInternal() {
    // Keep track of the unmint.
    LibFastHistory.data().supplyProofs.push(
      LibFastHistory.SupplyProof({
        op: LibFastHistory.SupplyOp.Burn,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  function supplyProofCount()
      external view returns(uint256) {
    return LibFastHistory.data().supplyProofs.length;
  }

  function paginateSupplyProofs(uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.SupplyProof[] memory, uint256) {
    return LibPaginate.supplyProofs(LibFastHistory.data().supplyProofs, cursor, perPage);
  }

  /// Transfer history-keeping methods.

  function transfered(address spender, address from, address to, uint256 amount, string calldata ref)
      external diamondInternal() {
    LibFastHistory.Data storage s = LibFastHistory.data();
    // Keep track of the transfer proof ID for the sender and for the recipient.
    s.transferProofInvolvements[from].push(s.transferProofs.length);
    s.transferProofInvolvements[to].push(s.transferProofs.length);
    // Keep track of the transfer proof globally.
    s.transferProofs.push(
      LibFastHistory.TransferProof({
        spender: spender,
        from: from,
        to: to,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  function transferProofCount()
      external view returns(uint256) {
    return LibFastHistory.data().transferProofs.length;
  }

  function paginateTransferProofs(uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.TransferProof[] memory, uint256) {
    return LibPaginate.transferProofs(LibFastHistory.data().transferProofs, cursor, perPage);
  }

  function transferProofByInvolveeCount(address involvee)
      external view returns(uint256) {
    return LibFastHistory.data().transferProofInvolvements[involvee].length;
  }

  function paginateTransferProofIndicesByInvolvee(address involvee, uint256 cursor, uint256 perPage)
      external view returns(uint256[] memory, uint256) {
    return LibPaginate.uint256s(LibFastHistory.data().transferProofInvolvements[involvee], cursor, perPage);
  }

  function paginateTransferProofsByInvolvee(address involvee, uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.TransferProof[] memory, uint256) {
    LibFastHistory.Data storage s = LibFastHistory.data();
    uint256[] storage collection  = s.transferProofInvolvements[involvee];
    uint256 length = (perPage > collection.length - cursor) ? collection.length - cursor : perPage;
    LibFastHistory.TransferProof[] memory values = new LibFastHistory.TransferProof[](length);
    for (uint256 i = 0; i < length; i++) {
      values[i] = s.transferProofs[collection[cursor + i]];
    }
    return (values, cursor + length);
  }
}
","keccak256":"0x5596e26868dce38659495b3acddbaec7045698d0ecc9039f1170c5a9f1e21cb1","license":"MIT"},"contracts/fast/FastTokenFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '../interfaces/IERC20.sol';
import '../interfaces/IERC1404.sol';
import '../lib/LibDiamond.sol';
import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastToken.sol';
import './FastTopFacet.sol';
import './FastAccessFacet.sol';
import './FastHistoryFacet.sol';
import './FastFrontendFacet.sol';


contract FastTokenFacet is AFastFacet, IERC20, IERC1404 {
  using LibAddressSet for LibAddressSet.Data;

  // Events.

  // Issuance related events.
  event Minted(uint256 indexed amount, string indexed ref);
  event Burnt(uint256 indexed amount, string indexed ref);

  // Transfer credits related events.
  event TransferCreditsAdded(address indexed spcMember, uint256 amount);
  event TransferCreditsDrained(address indexed spcMember, uint256 amount);

  /// Minting methods.

  function mint(uint256 amount, string calldata ref)
      external
      spcMembership {
    LibFastToken.Data storage s = LibFastToken.data();
    // We want to make sure that either of these two is true:
    // - The token doesn't have fixed supply.
    // - The token has fixed supply but has no tokens yet (First and only mint).
    require(
      !FastTopFacet(address(this)).hasFixedSupply() || (s.totalSupply == 0 && this.balanceOf(address(0)) == 0),
      LibConstants.REQUIRES_CONTINUOUS_SUPPLY
    );

    // Prepare the minted amount on the zero address.
    s.balances[address(0)] += amount;

    // Keep track of the minting operation.
    FastHistoryFacet(address(this)).minted(amount, ref);

    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit Minted(amount, ref);
  }

  function burn(uint256 amount, string calldata ref)
      external
      spcMembership {
    LibFastToken.Data storage s = LibFastToken.data();

    require(!FastTopFacet(address(this)).hasFixedSupply(), LibConstants.REQUIRES_CONTINUOUS_SUPPLY);
    require(balanceOf(address(0)) >= amount, LibConstants.INSUFFICIENT_FUNDS);

    // Remove the minted amount from the zero address.
    s.balances[address(0)] -= amount;

    // Keep track of the minting operation.
    FastHistoryFacet(address(this)).burnt(amount, ref);

    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit Burnt(amount, ref);
  }

  /// Tranfer Credit management.

  function transferCredits()
      external view returns(uint256) {
    return LibFastToken.data().transferCredits;
  }

  function addTransferCredits(uint256 amount)
      external
      spcMembership {
    LibFastToken.data().transferCredits += amount;
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit TransferCreditsAdded(msg.sender, amount);
  }

  function drainTransferCredits()
      external
      spcMembership {
    LibFastToken.Data storage s = LibFastToken.data();
    // Emit!
    emit TransferCreditsDrained(msg.sender, s.transferCredits);
    // Drain credits.
    s.transferCredits = 0;
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  /// ERC20 implementation and transfer related methods.

  function name()
      external view returns(string memory) {
    return LibFastToken.data().name;
  }

  function symbol()
      external view returns(string memory) {
    return LibFastToken.data().symbol;
  }

  function decimals()
      external view returns(uint256) {
    return LibFastToken.data().decimals;
  }

  function totalSupply()
      external override view returns(uint256) {
    return LibFastToken.data().totalSupply;
  }

  function balanceOf(address owner)
      public view override returns(uint256) {
    return LibFastToken.data().balances[owner];
  }

  function transfer(address to, uint256 amount)
      external override returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: msg.sender,
        to: to,
        amount: amount,
        ref: LibFastToken.DEFAULT_TRANSFER_REFERENCE
      })
    );
    return true;
  }

  function transferWithRef(address to, uint256 amount, string calldata ref)
      external {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: msg.sender,
        to: to,
        amount: amount,
        ref: ref
      })
    );
  }

  function allowance(address owner, address spender)
      public view override returns(uint256) {
    LibFastToken.Data storage s = LibFastToken.data();
    // If the allowance being queried is from the zero address and the spender
    // is a governor, we want to make sure that the spender has full rights over it.
    if (owner == address(0)) {
      require(FastAccessFacet(address(this)).isGovernor(spender), LibConstants.REQUIRES_FAST_GOVERNORSHIP);
      return s.balances[owner];
    }
    return s.allowances[owner][spender];
  }

  function approve(address spender, uint256 amount)
      external override returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performApproval(msg.sender, spender, amount);
    return true;
  }

  function disapprove(address spender)
      external
      membership(msg.sender) {
    // Make sure the call is performed externally so that we can mock.
    this.performDisapproval(msg.sender, spender);
  }

  function transferFrom(address from, address to, uint256 amount)
      external override returns(bool) {
    transferFromWithRef(from, to, amount, LibFastToken.DEFAULT_TRANSFER_REFERENCE);
    return true;
  }

  function transferFromWithRef(address from, address to, uint256 amount, string memory ref)
      public {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: from,
        to: to,
        amount: amount,
        ref: ref
      })
    );
  }

  /// Allowances query operations.

  function givenAllowanceCount(address owner)
      external view returns(uint256) {
    return LibFastToken.data().allowancesByOwner[owner].values.length;
  }

  function paginateAllowancesByOwner(address owner, uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastToken.data().allowancesByOwner[owner].values,
      index,
      perPage
    );
  }

  function receivedAllowanceCount(address spender)
      external view returns(uint256) {
    return LibFastToken.data().allowancesBySpender[spender].values.length;
  }

  function paginateAllowancesBySpender(address spender, uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastToken.data().allowancesBySpender[spender].values,
      index,
      perPage
    );
  }

  /// ERC1404 implementation.

  function detectTransferRestriction(address from, address to, uint256 amount)
      external view override returns(uint8) {
    LibFastToken.Data storage s = LibFastToken.data();
    if (s.transferCredits < amount) {
      return LibFastToken.INSUFFICIENT_TRANSFER_CREDITS_CODE;
    } else if (!FastAccessFacet(address(this)).isMember(from) ||
               !FastAccessFacet(address(this)).isMember(to)) {
      return FastTopFacet(address(this)).isSemiPublic()
        ? LibFastToken.REQUIRES_EXCHANGE_MEMBERSHIP_CODE
        : LibFastToken.REQUIRES_FAST_MEMBERSHIP_CODE;
    } else if (from == to) {
      return LibFastToken.REQUIRES_DIFFERENT_SENDER_AND_RECIPIENT_CODE;
    }
    return 0;
  }

  function messageForTransferRestriction(uint8 restrictionCode)
      external override pure returns(string memory) {
    if (restrictionCode == LibFastToken.INSUFFICIENT_TRANSFER_CREDITS_CODE) {
      return LibConstants.INSUFFICIENT_TRANSFER_CREDITS;
    } else if (restrictionCode == LibFastToken.REQUIRES_EXCHANGE_MEMBERSHIP_CODE) {
      return LibConstants.REQUIRES_EXCHANGE_MEMBERSHIP;
    } else if (restrictionCode == LibFastToken.REQUIRES_FAST_MEMBERSHIP_CODE) {
      return LibConstants.REQUIRES_FAST_MEMBERSHIP;
    } else if (restrictionCode == LibFastToken.REQUIRES_DIFFERENT_SENDER_AND_RECIPIENT_CODE) {
      return LibConstants.REQUIRES_DIFFERENT_SENDER_AND_RECIPIENT;
    }
    revert(LibConstants.UNKNOWN_RESTRICTION_CODE);
  }

  // These functions would be internal / private if we weren't using the diamond pattern.
  // Instead, they're `diamondInternal` - eg can only be called by facets of the current
  // FAST.

  struct TransferArgs {
    address spender;
    address from;
    address to;
    uint256 amount;
    string ref;
  }

  function performTransfer(TransferArgs calldata p)
      external diamondInternal
      canHoldTokens(p.from) canHoldTokens(p.to) differentAddresses(p.from, p.to) {
    LibFastToken.Data storage s = LibFastToken.data();

    // Make sure that there's enough funds.
    require(
      s.balances[p.from] >= p.amount,
      LibConstants.INSUFFICIENT_FUNDS
    );

    // If this is an allowance transfer...
    if (p.spender != p.from) {
      // Make sure that the spender has enough allowance.
      require(
        FastTokenFacet(address(this)).allowance(p.from, p.spender) >= p.amount,
        LibConstants.INSUFFICIENT_ALLOWANCE
      );

      // If the from account isn't the zero address...
      if (p.from != address(0)) {
        // Make sure enough credits exist.
        require(
          s.transferCredits >= p.amount,
          LibConstants.INSUFFICIENT_TRANSFER_CREDITS
        );

        // Decrease allowance.
        uint256 newAllowance = s.allowances[p.from][p.spender] -= p.amount;
        // If the allowance reached zero, we want to remove that allowance from
        // the various other places where we keep track of it.
        if (newAllowance == 0) {
          s.allowancesByOwner[p.from].remove(p.spender, true);
          s.allowancesBySpender[p.spender].remove(p.from, true);
        }
      }
    }

    // Keep track of the balances - `from` spends, `to` receives.
    s.balances[p.from] -= p.amount;
    s.balances[p.to] += p.amount;

    // If the funds are not moving from the zero address, decrease transfer credits.
    if (p.from != address(0)) {
      s.transferCredits -= p.amount;
    }

    // If the funds are going to the ZERO address, decrease total supply.
    if (p.to == address(0)) {
      s.totalSupply -= p.amount;
      // If funds at address zero changed, we can emit a top-level details change event.
      FastFrontendFacet(address(this)).emitDetailsChanged();
    }
    // If the funds are moving from the zero address, increase total supply.
    else if (p.from == address(0)) {
      s.totalSupply += p.amount;
      // If funds at address zero changed, we can emit a top-level details change event.
      FastFrontendFacet(address(this)).emitDetailsChanged();
    }

    // Keep track of the transfer in the history facet.
    FastHistoryFacet(address(this)).transfered(p.spender, p.from, p.to, p.amount, p.ref);

    // Emit!
    emit IERC20.Transfer(p.from, p.to, p.amount);
  }

  function performApproval(address from, address spender, uint256 amount)
      external
      diamondInternal
      canHoldTokens(from) {
    LibFastToken.Data storage s = LibFastToken.data();

    // Store allowance...
    s.allowances[from][spender] += amount;
    // Keep track of given and received allowances.
    s.allowancesByOwner[from].add(spender, true);
    s.allowancesBySpender[spender].add(from, true);

    // Emit!
    emit IERC20.Approval(from, spender, amount);
  }

  function performDisapproval(address from, address spender)
      external
      diamondInternal {
    LibFastToken.Data storage s = LibFastToken.data();

    // Remove allowance.
    s.allowances[from][spender] = 0;
    s.allowancesByOwner[from].remove(spender, false);
    s.allowancesBySpender[spender].remove(from, false);

    // Emit!
    emit IERC20.Disapproval(from, spender);
  }

  // WARNING: This function contains two loops. We know that this should never
  // happen in solidity. However:
  // - In the context of our private chain, gas is cheap.
  // - It can only be called by a governor.
  function beforeRemovingMember(address member)
      external diamondInternal() {
    require(balanceOf(member) == 0, 'Balance is positive');

    LibFastToken.Data storage s = LibFastToken.data();

    // Remove all given allowances.
    address[] storage gaData = s.allowancesByOwner[member].values;
    while (gaData.length > 0) {
      // Make sure the call is performed externally so that we can mock.
      this.performDisapproval(member, gaData[0]);
    }

    // Remove all received allowances.
    address[] storage raData = s.allowancesBySpender[member].values;
    while (raData.length > 0) {
      // Make sure the call is performed externally so that we can mock.
      this.performDisapproval(raData[0], member);
    }
  }

  // Modifiers.

  modifier differentAddresses(address a, address b) {
    require(a != b, LibConstants.REQUIRES_DIFFERENT_SENDER_AND_RECIPIENT);
    _;
  }
}
","keccak256":"0x0477d763d2d0dfa8474f0f1b1050c25245d5b4b5b91e698d3059e3d325b55861","license":"MIT"},"contracts/fast/FastTopFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '../lib/LibConstants.sol';
import '../lib/LibHelpers.sol';
import './lib/AFastFacet.sol';
import './lib/LibFast.sol';
import './FastFrontendFacet.sol';

contract FastTopFacet is AFastFacet {

  // Eth provisioning related events.
  event EthReceived(address indexed from, uint256 amount);
  event EthDrained(address indexed to, uint256 amount);

  // Getters and setters for global flags.

  function spcAddress()
      external view returns(address) {
    return LibFast.data().spc;
  }

  function exchangeAddress()
      external view returns(address) {
    return LibFast.data().exchange;
  }

    function isSemiPublic()
      external view returns(bool) {
    return LibFast.data().isSemiPublic;
  }

  function hasFixedSupply()
      external view returns(bool) {
    return LibFast.data().hasFixedSupply;
  }

  // Setters for global flags.

  /// @dev Allows to switch from a private scheme to a semi-public scheme, but not the other way around.
  function setIsSemiPublic(bool flag)
      external
      spcMembership {
    LibFast.Data storage s = LibFast.data();
    // Someone is trying to toggle back to private?... No can do!isSemiPublic
    require(!this.isSemiPublic() || this.isSemiPublic() == flag, LibConstants.UNSUPPORTED_OPERATION);
    s.isSemiPublic = flag;
  }

  // Provisioning functions.

  function provisionWithEth()
      external payable {
    require(msg.value > 0, LibConstants.MISSING_ATTACHED_ETH);
    emit EthReceived(msg.sender, msg.value);
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  function drainEth()
      external
      spcMembership {
    uint256 amount = payable(address(this)).balance;
    payable(msg.sender).transfer(amount);
    emit EthDrained(msg.sender, amount);
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  /**
  * @dev This function allows contracts of the FAST network to request ETH
  * provisioning to arbitrary addresses.
  */
  function payUpTo(address payable recipient, uint256 amount)
      external diamondInternal() {
    require(recipient != address(0), LibConstants.REQUIRES_NON_ZERO_ADDRESS);
    amount = LibHelpers.upTo(recipient, amount);
    // Transfer some eth!
    if (amount != 0) { recipient.transfer(amount); }
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }
}
