:"function"},{"inputs":[],"name":"totalSupply","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"to","type":"address"},{"internalType":"uint256","name":"amount","type":"uint256"}],"name":"transfer","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"from","type":"address"},{"internalType":"address","name":"to","type":"address"},{"internalType":"uint256","name":"amount","type":"uint256"}],"name":"transferFrom","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"from","type":"address"},{"internalType":"address","name":"to","type":"address"},{"internalType":"uint256","name":"amount","type":"uint256"},{"internalType":"string","name":"ref","type":"string"}],"name":"transferFromWithRef","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"to","type":"address"},{"internalType":"uint256","name":"amount","type":"uint256"},{"internalType":"string","name":"ref","type":"string"}],"name":"transferWithRef","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"kind":"dev","methods":{"allowance(address,address)":{"details":"This value changes when `approve`, `disapprove` and `transferFrom` / `transferFromWithRef` are called.","params":{"owner":"is the owner of the funds.","spender":"is the address for which the allowance should be queried."},"returns":{"_0":"A `uint256` representing the remaining allowance of `spender` over `owner`'s funds."}},"approve(address,uint256)":{"params":{"amount":"is how much to **increase** the allowance.","spender":"is the address to allow spending from the caller's wallet."}},"balanceOf(address)":{"params":{"owner":"The owners address to get the balance of."},"returns":{"_0":"uint256 The current balance of this owner's account."}},"burn(uint256,string)":{"params":{"amount":"The number of FAST tokens to mint.","ref":"A reference for this minting operation."}},"decimals()":{"returns":{"_0":"uint256 Number of decimals the FAST has."}},"disapprove(address,uint256)":{"params":{"amount":"is how much to **decrease** the allowance.","spender":"is the address to disallow spending from the caller's wallet."}},"mint(uint256,string)":{"params":{"amount":"The number of FAST tokens to mint.","ref":"A reference for this minting operation."}},"name()":{"returns":{"_0":"string Name of the FAST."}},"performApproval(address,address,uint256)":{"params":{"amount":"is how much to **increase** the current allowance by.  Note: This function runs when amount is zero, and will emit.","from":"is the wallet from which to give the allowance.","spender":"is the receiver of the allowance."}},"retrieveDeadTokens(address)":{"params":{"holder":"is the address for which to move the tokens from."}},"symbol()":{"returns":{"_0":"string Symbol of the FAST."}},"totalSupply()":{"returns":{"_0":"uint256 Total supply of the FAST."}}},"version":1},"userdoc":{"events":{"Approval(address,address,uint256)":{"notice":"See `ERC20.Approval`."},"Burnt(uint256,string)":{"notice":"Emited whenever an burning happens in a FAST."},"DetailsChanged(bool,uint256,uint256,uint256,uint256,uint256)":{"notice":"This is an event that is fired whenever any of some of the FAST parameters change, so that the frontend can react to it and refresh the general header for that fast as well as the baseball cards in the FASTs list."},"Disapproval(address,address,uint256)":{"notice":"See `ERC20.Disapproval`."},"GovernorAdded(address)":{"notice":"Emited when a governor is added to the implementing contract."},"GovernorRemoved(address)":{"notice":"Emited when a governor is removed to the implementing contract."},"MemberAdded(address)":{"notice":"Emited when a member is added to the implementing contract."},"MemberRemoved(address)":{"notice":"Emited when a member is removed to the implementing contract."},"Minted(uint256,string)":{"notice":"Emited whenever an issuance happens in a FAST."},"Transfer(address,address,uint256)":{"notice":"See `ERC20.Transfer`."}},"kind":"user","methods":{"allowance(address,address)":{"notice":"Returns the remaining number of tokens that `spender` will be allowed to spend on behalf of `owner` through `transferFrom`. This is zero by default."},"approve(address,uint256)":{"notice":"This method directly calls `performApproval`, setting its `from` paramter to the sender of the transaction."},"balanceOf(address)":{"notice":"The balance of the passed owner (ERC20 standard)."},"burn(uint256,string)":{"notice":"Burns an amount of FAST tokens.  A reference can be passed to identify why this happened for example. Business logic. - Modifiers:   - Requires the caller to be a member of the Issuer contract. - Requires that the token has continuous supply. - Requires that there are enough funds in the reserve to cover for `amount` being burnt. - Decreases the reserve balance by `amount`. - Calls `FastHistoryFacet.burnt(amount, ref)`. - Calls `FastFrontendFacet.emitDetailsChanged`. - Emits a `Burnt(amount, ref)`."},"decimals()":{"notice":"The `decimals` of this FAST (ERC20 standard)."},"disapprove(address,uint256)":{"notice":"This method directly calls `performDisapproval`, setting its `from` parameter to the sender of the transaction."},"mint(uint256,string)":{"notice":"Mints an amount of FAST tokens.  A reference can be passed to identify why this happened for example. Business logic: - Modifiers:   - Requires the caller to be a member of the Issuer contract. - Requires that either the token has continuous supply, or that no tokens have been minted yet. - Increases the reserve balance by `amount`. - Calls `FastHistoryFacet.minted`. - Calls `FastFrontendFacet.emitDetailsChanged`. - Emits a `Minted(amount, ref)` event."},"name()":{"notice":"The name of this FAST (ERC20 standard)."},"performApproval(address,address,uint256)":{"notice":"Increases the allowance given by `from` to `spender` by `amount`. Note that this function should run and emit even if the amount passed is zero. Business logic: - Modifiers:   - Only facets of the current diamond should be able to call this.   - Requires that `onlyTokenHolder` passes for the `from` address. - Requires that the `amount` is positive number. - Increases the allowance given by `from` to `spender` by `amount`. - Update the allowance lookup tables in both directions. - Emits an `Approval(from, spender, amount)`."},"performDisapproval(address,address,uint256)":{"notice":"Decreases allowance given by `from` to `spender` by `amount`. Business logic: - Modifiers:   - Only facets of the current diamond should be able to call this. - The allowance given by `from` to `spender` is decreased by `amount`. - Whether the allowance reached zero, stop tracking it by owner and by spender. - Emit a `Disapproval(from, spender, amount)` event.  Note: This function runs when amount is zero, and will emit."},"performTransfer((address,address,address,uint256,string))":{"notice":"This is the internal method that gets called whenever a transfer is initiated. Both `transfer`, `transferWithRef`, and their variants internally call this function. Business logic: - Modifiers:   - Only facets of the current diamond should be able to call this. - Requires that transfers are enabled for this FAST. - Requires that `from` and `to` are different addresses. - Requires that `from` membership is active in the marketplace. - If `from` is not the reserve, requires that `from` is a valid token holder. - If `from` is the reserve, requires that the message sender is an issuer member. - Requires that `to` is a valid token holder. - Requires that the amount is a positive value. - If the transfer is an allowance - e.g. the `spender` is not the same as the `from` address,   - The allowance given by the `from` address to the `spender` covers for the `amount`.     - If we are **not** transfering **from** the reserve,       - Decreases the allowance given by `from` to `spender`.         - If the new allowance reaches zero,           - Stop tracking the allowance in the allowance lookup tables for both spending and receiving directions. - Decreases the balance of the `owner` address. - Increases the balance of the `to` address by `amount`. - If we are **not** transfering **from** the reserve,   - Requires that there are enough transfer credits to cover for `amount`.   - Decreases the transfer credits by `amount`. - If the `to` address is the reserve,   - Decreases the total supply by `amount`.   - Calls `FastFrontendFacet.emitDetailsChanged`. - Else, if the `from` address is the reserve,   - Increases the total supply by `amount`.   - Calls `FastFrontendFacet.emitDetailsChanged`. - Calls `FastHistoryFacet.transfered`. - Emits a `Transfer(from, to, amount)` event."},"retrieveDeadTokens(address)":{"notice":"Allows an Issuer member to move an arbitrary account's holdings back to the reserve, as per regulatory requirements. Business logic: - Modifiers:   - Requires that the caller is a member of the Issuer contract. - If the amount held by `holder` is not zero   - The balance of `holder` should be set to zero.   - The reserve's balance should be increased by how much was on the holder's account.   - Total supply should be decreased by that amount too. - The `holder`'s address should not be tracked as a token holder in this FAST anymore. - The `holder`'s address should not be tracked as a token holder in the Marketplace anymore. - A `Transfer(holder, reserve, amount)` event should be emited. - If the amount previously held by `holder` was not zero,   - Since the reserve balance and total supply have changed, the `FastFrontendFacet.emitDetailsChanged()` function should be called."},"symbol()":{"notice":"The symbol of this FAST (ERC20 standard)."},"totalSupply()":{"notice":"The total supply of the FAST (ERC20 standard)."},"transfer(address,uint256)":{"notice":"See `performTransfer`, the spender will be equal to the `owner`, and the `ref` will be defauted. "},"transferFrom(address,address,uint256)":{"notice":"See `performTransfer`, the `ref` will be defaulted."},"transferFromWithRef(address,address,uint256,string)":{"notice":"See `performTransfer`."},"transferWithRef(address,uint256,string)":{"notice":"See `performTransfer`, the spender will be equal to the `owner`. "}},"version":1}},"settings":{"compilationTarget":{"contracts/fast/FastTokenFacet.sol":"FastTokenFacet"},"evmVersion":"london","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":1000000},"remappings":[]},"sources":{"contracts/fast/FastAccessFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../interfaces/IHasMembers.sol';
import '../interfaces/IHasGovernors.sol';
import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import '../marketplace/MarketplaceAccessFacet.sol';
import '../issuer/IssuerAccessFacet.sol';
import './FastTokenFacet.sol';
import './lib/AFastFacet.sol';
import './lib/LibFast.sol';
import './lib/LibFastAccess.sol';
import './FastTopFacet.sol';
import './FastFrontendFacet.sol';


/**
 * @notice The FAST Access facet is the source of truth when it comes to
 * permissioning and ACLs within a given FAST.
 */
contract FastAccessFacet is AFastFacet, IHasMembers, IHasGovernors {
  using LibAddressSet for LibAddressSet.Data;
  // Structs.

  /**
   * @notice This structure isn't used anywhere in storage. Instead, it
   * allows various methods of the contract to return all the flags
   * associated with a given address in one go.
   */
  struct Flags {
    /// @notice Whether or not the item in scope is considered a governor of this FAST.
    bool isGovernor;
    /// @notice Whether or not the item in scope is considered a member of this FAST.
    bool isMember;
  }

  // Governorship related stuff.

  /// @notice See `IHasGovernors`.
  function isGovernor(address candidate)
      external view override returns(bool) {
    return LibFastAccess.data().governorSet.contains(candidate);
  }

   /// @notice See `IHasGovernors`.
  function governorCount()
      external override view returns(uint256) {
    return LibFastAccess.data().governorSet.values.length;
  }

   /// @notice See `IHasGovernors`.
  function paginateGovernors(uint256 index, uint256 perPage)
      external override view returns(address[] memory, uint256) {
    return LibPaginate.addresses(LibFastAccess.
      data().governorSet.values,
      index,
      perPage
    );
  }

   /// @notice See `IHasGovernors`.
  function addGovernor(address payable governor)
      external override
      onlyIssuerMember
      onlyMarketplaceMember(governor) {
    // Add governor to list.
    LibFastAccess.data().governorSet.add(governor, false);
    // Notify issuer that this member was added to this FAST.
    IssuerAccessFacet(LibFast.data().issuer).governorAddedToFast(governor);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit GovernorAdded(governor);
  }

   /// @notice See `IHasGovernors`.
  function removeGovernor(address governor)
      external override
      onlyIssuerMember {
    // Remove governor.
    LibFastAccess.data().governorSet.remove(governor, false);
    // Notify issuer that this member was removed from this FAST.
    IssuerAccessFacet(LibFast.data().issuer).governorRemovedFromFast(governor);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit GovernorRemoved(governor);
  }

  /// Membership related stuff.

   /// @notice See `IHasMembers`.
  function isMember(address candidate)
      external override view returns(bool) {
    return LibFastAccess.data().memberSet.contains(candidate);
  }

   /// @notice See `IHasMembers`.
  function memberCount()
      external override view returns(uint256) {
    return LibFastAccess.data().memberSet.values.length;
  }

   /// @notice See `IHasMembers`.
  function paginateMembers(uint256 index, uint256 perPage)
      external override view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastAccess.data().memberSet.values,
      index,
      perPage
    );
  }

   /// @notice See `IHasMembers`.
  function addMember(address payable member)
      external override 
      onlyGovernor(msg.sender) onlyMarketplaceMember(member) {
    // Add the member.
    LibFastAccess.data().memberSet.add(member, false);
    // Notify marketplace that this member was added to this FAST.
    MarketplaceAccessFacet(LibFast.data().marketplace).memberAddedToFast(member);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit MemberAdded(member);
  }

   /// @notice See `IHasMembers`.
  function removeMember(address member)
      external override 
      onlyGovernor(msg.sender) {
    // Remove member.
    LibFastAccess.data().memberSet.remove(member, false);
    // Notify token facet that this member was removed.
    FastTokenFacet(address(this)).beforeRemovingMember(member);
    // Notify marketplace that this member was removed from this FAST.
    MarketplaceAccessFacet(LibFast.data().marketplace).memberRemovedFromFast(member);
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit MemberRemoved(member);
  }

  /// Flags.

  /**
   * @notice Retrieves flags for a given address.
   * @param a is the address to retrieve flags for.
   * @return A `Flags` struct.
   */
  function flags(address a)
      external view returns(Flags memory) {
    LibFastAccess.Data storage s = LibFastAccess.data();
    return
      Flags({
        isGovernor: s.governorSet.contains(a),
        isMember: s.memberSet.contains(a)
      });
  }
}
