pragma solidity 0.8.10;

import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastAccess.sol';
import './lib/LibFastToken.sol';


/**
 * @notice A facet dedicated to view / UI only methods. This facet should never hold any method that
 * is not either `pure` or `view`, except to emit events.
 */
contract FastFrontendFacet is AFastFacet {
  using LibAddressSet for LibAddressSet.Data;

  // Data structures.

  /**
   * @notice This struct groups the common attributes of a FAST.
   * @dev This struct shouldn't be used in internal storage.
   */
  struct Details {
    /// @notice The `address` of the FAST.
    address addr;
    /// @notice The `name` of the FAST (ERC20).
    string name;
    /// @notice The `symbol` of the FAST (ERC20).
    string symbol;
    /// @notice The `decimals` of the FAST (ERC20).
    uint8 decimals;
    /// @notice The `totalSupply` of the FAST (ERC20).
    uint256 totalSupply;
    /// @notice Whether the FAST is semi public or not.
    bool isSemiPublic;
    /// @notice Whether the FAST has a fixed supply or continious.
    bool hasFixedSupply;
    /// @notice Whether the transfers are enabled or not for this FAST.
    bool transfersDisabled;
    /// @notice The reserve balance.
    uint256 reserveBalance;
    /// @notice The Ether balance.
    uint256 ethBalance;
    /// @notice The number of members the FAST has.
    uint256 memberCount;
    /// @notice The number of governors for the FAST.
    uint256 governorCount;
  }

  /**
   * @notice Member level details.
   * @dev This struct shouldn't be used in internal storage.
   */
  struct MemberDetails {
    /// @notice The Member's address.
    address addr;
    /// @notice The Member's balance.
    uint256 balance;
    uint256 ethBalance;
    /// @notice Whether the Member is also a Governor.
    bool isGovernor;
  }

  /**
   * @notice Governor level details.
   * @dev Note that **this struct shouldn't be used in internal storage**.
   */
  struct GovernorDetails {
    /// @notice The Governor's address.
    address addr;
    uint256 ethBalance;
    /// @notice Whether the Governor is also a Member.
    bool isMember;
  }

  // Emitters.

  /**
   * @notice Called by diamond facets, signals that FAST details may have changed.
   * 
   * Business logic:
   * - Modifiers:
   *   - Requires the caller to be another facet of the diamond.
   * Emits `DetailsChanged`, see `IFastEvents.DetailsChanged`
   */
  function emitDetailsChanged()
      external onlyDiamondFacet {
    LibFastAccess.Data storage accessData = LibFastAccess.data();
    LibFastToken.Data storage tokenData = LibFastToken.data();
    emit DetailsChanged({
      transfersDisabled: LibFast.data().transfersDisabled,
      memberCount: accessData.memberSet.values.length,
      governorCount: accessData.governorSet.values.length,
      totalSupply: tokenData.totalSupply,
      reserveBalance: tokenData.balances[LibConstants.ZERO_ADDRESS],
      ethBalance: payable(address(this)).balance
    });
  }

  // Public functions.

  /**
   * @notice Gets the details of a FAST.
   * @return The details for the current FAST, see `Details`.
   */
  function details()
      public view returns(Details memory) {
    LibFast.Data storage topStorage = LibFast.data();
    LibFastAccess.Data storage accessStorage = LibFastAccess.data();
    LibFastToken.Data storage tokenStorage = LibFastToken.data();
    return Details({
      addr: address(this),
      name: tokenStorage.name,
      symbol: tokenStorage.symbol,
      decimals: tokenStorage.decimals,
      totalSupply: tokenStorage.totalSupply,
      isSemiPublic: topStorage.isSemiPublic,
      hasFixedSupply: topStorage.hasFixedSupply,
      transfersDisabled: topStorage.transfersDisabled,
      reserveBalance: tokenStorage.balances[LibConstants.ZERO_ADDRESS],
      ethBalance: payable(address(this)).balance,
      memberCount: accessStorage.memberSet.values.length,
      governorCount: accessStorage.governorSet.values.length
    });
  }

  /**
   * @notice Gets detailed member details.
   * @return A FAST member's details, see `MemberDetails`.
   */
  function detailedMember(address member)
      public view returns(MemberDetails memory) {
    return MemberDetails({
      addr: member,
      balance: LibFastToken.data().balances[member],
      ethBalance: member.balance,
      isGovernor: LibFastAccess.data().governorSet.contains(member)
    });
  }

  /**
   * @notice Gets detailed governor details.
   * @return GovernorDetails See: `GovernorDetails`.
   */
  function detailedGovernor(address governor)
      public view returns(GovernorDetails memory) {
    return GovernorDetails({
      addr: governor,
      ethBalance: governor.balance,
      isMember: LibFastAccess.data().memberSet.contains(governor)
    });
  }

  function paginateDetailedMembers(uint256 index, uint256 perPage)
      external view returns(MemberDetails[] memory, uint256) {
    (address[] memory members, uint256 nextCursor) =
      LibPaginate.addresses(LibFastAccess.data().memberSet.values, index, perPage);
    MemberDetails[] memory values = new MemberDetails[](members.length);
    uint256 length = members.length;
    for (uint256 i = 0; i < length;) {
      values[i] = detailedMember(members[i]);
      unchecked { ++i; }
    }
    return (values, nextCursor);
  }

  function paginateDetailedGovernors(uint256 index, uint256 perPage)
      external view returns(GovernorDetails[] memory, uint256) {
    (address[] memory governors, uint256 nextCursor) =
      LibPaginate.addresses(LibFastAccess.data().governorSet.values, index, perPage);
    GovernorDetails[] memory values = new GovernorDetails[](governors.length);
    uint256 length =  governors.length;
    for (uint256 i = 0; i < length;) {
      values[i] = detailedGovernor(governors[i]);
      unchecked { ++i; }
    }
    return (values, nextCursor);
  }
}
","keccak256":"0x4e41534dc0e2b758183520b32b9123d4a388ad425af86a508ff833b41e96e17f","license":"MIT"},"contracts/fast/FastHistoryFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastHistory.sol';


/**
 * @notice Although past events could be scrapped from the chain, we want to
 * the frontend to be capable of listing past transfers and minting / burning events.
 * This facet is in charge of performing archival of these things.
 */
contract FastHistoryFacet is AFastFacet {
  /// Minting history-keeping methods.

  /**
   * @notice This method is a callback for other facets to signal whenever new tokens are minted.
   * 
   * Business logic:
   * - Requires that the caller must be another facet.
   * - Adds a supply proof item of type `LibFastHistory.SupplyOp.Mint` on top of the stack.
   */
  function minted(uint256 amount, string calldata ref)
      external onlyDiamondFacet() {
    // Keep track of the mint.
    LibFastHistory.data().supplyProofs.push(
      LibFastHistory.SupplyProof({
        op: LibFastHistory.SupplyOp.Mint,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  /**
   * @notice This method is a callback for other facets to signal whenever new tokens are burnt.
   * 
   * Business logic:
   * - Requires that the caller must be another facet.
   * - Adds a supply proof item of type `LibFastHistory.SupplyOp.Burn` on top of the stack.
   */
  function burnt(uint256 amount, string calldata ref)
      external onlyDiamondFacet() {
    // Keep track of the unmint.
    LibFastHistory.data().supplyProofs.push(
      LibFastHistory.SupplyProof({
        op: LibFastHistory.SupplyOp.Burn,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  /**
   * @notice Returns the number of supply proofs (minting and burning together) ever created.
   * @return A `uint256`.
   */
  function supplyProofCount()
      external view returns(uint256) {
    return LibFastHistory.data().supplyProofs.length;
  }

  /**
   * @notice Returns a page of supply proofs (minting and burning together).
   * @param cursor is the zero-based index where to start fetching records.
   * @param perPage is the number of items to return.
   * @return A `(LibFastHistory.SupplyProof[], uint256)` tuple containing a page of data and the cursor to the next page.
   */
  function paginateSupplyProofs(uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.SupplyProof[] memory, uint256) {
    return LibPaginate.supplyProofs(LibFastHistory.data().supplyProofs, cursor, perPage);
  }

  /// Transfer history-keeping methods.

  /**
   * @notice This method is a callback for other facets to signal whenever a transfer has completed successfuly.
   * 
   * Business logic:
   * - Requires that the caller must be another facet.
   * - Keeps track of the operation in various tracking structures, so that it can be queried later by `sender` and `recipient`.
   * - Pushes a transfer proof to the main transfer proof tracking stack.
   */
  function transfered(address spender, address from, address to, uint256 amount, string calldata ref)
      external onlyDiamondFacet() {
    LibFastHistory.Data storage s = LibFastHistory.data();
    // Keep track of the transfer proof ID for the sender and for the recipient.
    s.transferProofInvolvements[from].push(s.transferProofs.length);
    s.transferProofInvolvements[to].push(s.transferProofs.length);
    // Keep track of the transfer proof globally.
    s.transferProofs.push(
      LibFastHistory.TransferProof({
        spender: spender,
        from: from,
        to: to,
        amount: amount,
        blockNumber: block.number,
        ref: ref
      })
    );
  }

  /**
   * @notice Returns the number of transfer proofs ever created.
   * @return A `uint256`.
   */
  function transferProofCount()
      external view returns(uint256) {
    return LibFastHistory.data().transferProofs.length;
  }

  /**
   * @notice Returns a page of transfer proofs.
   * @param cursor is the zero-based index where to start fetching records.
   * @param perPage is the number of items to return.
   * @return A `(LibFastHistory.TransferProof[], uint256)` tuple containing a page of data and the cursor to the next page.
   */
  function paginateTransferProofs(uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.TransferProof[] memory, uint256) {
    return LibPaginate.transferProofs(LibFastHistory.data().transferProofs, cursor, perPage);
  }

  /**
   * @notice Counts all past inbound and outbound transfers involving a given address.
   * @param involvee is the address for which to get the transfer proofs.
   */
  function transferProofByInvolveeCount(address involvee)
      external view returns(uint256) {
    return LibFastHistory.data().transferProofInvolvements[involvee].length;
  }

  /**
   * @notice Returns pages of indices of past inbound and outbound transfer proofs by involvee.
   * @dev This function is reading from an indexing data structure. Each index points to a record
   * in the main transfer proof storage, and can then be found in `transferProofs` at returned indices.
   * @param involvee is the address for which to retrieve a page of data.
   * @param cursor is where to start.
   * @param perPage is how many records at most should be returned.
  */
  function paginateTransferProofIndicesByInvolvee(address involvee, uint256 cursor, uint256 perPage)
      external view returns(uint256[] memory, uint256) {
    return LibPaginate.uint256s(LibFastHistory.data().transferProofInvolvements[involvee], cursor, perPage);
  }

  /**
   * @notice Returns a page of inbound and outbound transfer proofs based on an involvee.#
   * @param involvee is the address for which to fetch the data.
   * @param cursor is where to start.
   * @param perPage is how many items at most to return.
   * @return A `(LibFastHistory.TransferProof[], uint256)` tuple containing the results and the cursor to the next page.
   */
  function paginateTransferProofsByInvolvee(address involvee, uint256 cursor, uint256 perPage)
      external view returns(LibFastHistory.TransferProof[] memory, uint256) {
    LibFastHistory.Data storage s = LibFastHistory.data();
    uint256[] storage collection  = s.transferProofInvolvements[involvee];
    uint256 length = (perPage > collection.length - cursor) ? collection.length - cursor : perPage;
    LibFastHistory.TransferProof[] memory values = new LibFastHistory.TransferProof[](length);
    for (uint256 i = 0; i < length;) {
      values[i] = s.transferProofs[collection[cursor + i]];
      unchecked { ++i; }
    }
    return (values, cursor + length);
  }
}
","keccak256":"0x62822d72ec1fe8073dca0c1853a4fd2e5709c91875fd30416d229c80f5f9de28","license":"MIT"},"contracts/fast/FastTokenFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../interfaces/IERC20.sol';
import '../interfaces/ICustomErrors.sol';
import '../interfaces/IHasMembers.sol';
import '../interfaces/IHasGovernors.sol';
import '../lib/LibDiamond.sol';
import '../lib/LibAddressSet.sol';
import '../lib/LibPaginate.sol';
import './lib/AFastFacet.sol';
import './lib/LibFastToken.sol';
import './lib/IFast.sol';
import '../marketplace/MarketplaceTokenHoldersFacet.sol';
import './FastTopFacet.sol';
import './FastAccessFacet.sol';
import './FastHistoryFacet.sol';
import './FastFrontendFacet.sol';


contract FastTokenFacet is AFastFacet, IERC20 {
  using LibAddressSet for LibAddressSet.Data;

  // Minting methods.

  /**
   * @notice Mints an amount of FAST tokens.
   *  A reference can be passed to identify why this happened for example.
   *
   * Business logic:
   * - Modifiers:
   *   - Requires the caller to be a member of the Issuer contract.
   * - Requires that either the token has continuous supply, or that no tokens have been minted yet.
   * - Increases the reserve balance by `amount`.
   * - Calls `FastHistoryFacet.minted`.
   * - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Emits a `Minted(amount, ref)` event.
   * @param amount The number of FAST tokens to mint.
   * @param ref A reference for this minting operation.
   */
  function mint(uint256 amount, string calldata ref)
      external
      onlyIssuerMember {
    LibFastToken.Data storage s = LibFastToken.data();
    // We want to make sure that either of these two is true:
    // - The token doesn't have fixed supply.
    // - The token has fixed supply but has no tokens yet (First and only mint).
    if (FastTopFacet(address(this)).hasFixedSupply() && (s.totalSupply != 0 || this.balanceOf(address(0)) != 0))
      revert ICustomErrors.RequiresContinuousSupply();

    // Prepare the minted amount on the zero address.
    s.balances[address(0)] += amount;

    // Keep track of the minting operation.
    FastHistoryFacet(address(this)).minted(amount, ref);

    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit Minted(amount, ref);
  }

  /**
   * @notice Burns an amount of FAST tokens.
   *  A reference can be passed to identify why this happened for example.
   *
   * Business logic.
   * - Modifiers:
   *   - Requires the caller to be a member of the Issuer contract.
   * - Requires that the token has continuous supply.
   * - Requires that there are enough funds in the reserve to cover for `amount` being burnt.
   * - Decreases the reserve balance by `amount`.
   * - Calls `FastHistoryFacet.burnt(amount, ref)`.
   * - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Emits a `Burnt(amount, ref)`.
   * @param amount The number of FAST tokens to mint.
   * @param ref A reference for this minting operation.
   */
  function burn(uint256 amount, string calldata ref)
      external
      onlyIssuerMember {
    LibFastToken.Data storage s = LibFastToken.data();

    if (FastTopFacet(address(this)).hasFixedSupply())
      revert ICustomErrors.RequiresContinuousSupply();

    // Remove the minted amount from the zero address.
    s.balances[address(0)] -= amount;

    // Keep track of the minting operation.
    FastHistoryFacet(address(this)).burnt(amount, ref);

    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
    emit Burnt(amount, ref);
  }

  /**
   * @notice Allows an Issuer member to move an arbitrary account's holdings back to the reserve,
   * as per regulatory requirements.
   *
   * Business logic:
   * - Modifiers:
   *   - Requires that the caller is a member of the Issuer contract.
   * - If the amount held by `holder` is not zero
   *   - The balance of `holder` should be set to zero.
   *   - The reserve's balance should be increased by how much was on the holder's account.
   *   - Total supply should be decreased by that amount too.
   * - The `holder`'s address should not be tracked as a token holder in this FAST anymore.
   * - The `holder`'s address should not be tracked as a token holder in the Marketplace anymore.
   * - A `Transfer(holder, reserve, amount)` event should be emited.
   * - If the amount previously held by `holder` was not zero,
   *   - Since the reserve balance and total supply have changed, the `FastFrontendFacet.emitDetailsChanged()` function should be called.
   * @param holder is the address for which to move the tokens from.
   */
  function retrieveDeadTokens(address holder)
      external
      onlyIssuerMember {
    // Cache how many tokens the holder has.
    uint256 amount = balanceOf(holder);
    // Note: The amount **can** be zero in this function.

    // Grab a pointer to the token storage.
    LibFastToken.Data storage s = LibFastToken.data();

    // These should only run if the amount is zero, as they result in a no-op.
    if (amount > 0) {
      // Set the holder balance to zero.
      s.balances[holder] = 0;
      // Increment the reserve's balance.
      s.balances[address(0)] += amount;
      // The tokens aren't in circulation anymore - decrease total supply.
      s.totalSupply -= amount;
    }

    // Since the holder's account is now empty, make sure to keep track of it both
    // in this FAST and in the marketplace.
    s.tokenHolders.remove(holder, true);
    MarketplaceTokenHoldersFacet(LibFast.data().marketplace).fastBalanceChanged(holder, 0);

    // This operation can be seen as a regular transfer between holder and reserve. Emit.
    emit Transfer(holder, address(0), amount);

    // If amount wasn't zero, total supply and reserve balance have changed - emit.
    if (amount > 0)
      FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  // ERC20 implementation and transfer related methods.

  /**
   * @notice The name of this FAST (ERC20 standard).
   * @return string Name of the FAST.
   */
  function name()
      external view returns(string memory) {
    return LibFastToken.data().name;
  }

  /**
   * @notice The symbol of this FAST (ERC20 standard).
   * @return string Symbol of the FAST.
   */
  function symbol()
      external view returns(string memory) {
    return LibFastToken.data().symbol;
  }

  /**
   * @notice The `decimals` of this FAST (ERC20 standard).
   * @return uint256 Number of decimals the FAST has.
   */
  function decimals()
      external view returns(uint256) {
    return LibFastToken.data().decimals;
  }

  /**
   * @notice The total supply of the FAST (ERC20 standard).
   * @return uint256 Total supply of the FAST.
   */
  function totalSupply()
      external override view returns(uint256) {
    return LibFastToken.data().totalSupply;
  }

  /**
   * @notice The balance of the passed owner (ERC20 standard).
   * @param owner The owners address to get the balance of.
   * @return uint256 The current balance of this owner's account.
   */
  function balanceOf(address owner)
      public view override returns(uint256) {
    return LibFastToken.data().balances[owner];
  }

  /**
   * @notice See `performTransfer`, the spender will be equal to the `owner`, and the `ref` will be defauted. */
  function transfer(address to, uint256 amount)
      external override returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: msg.sender,
        to: to,
        amount: amount,
        ref: LibFastToken.DEFAULT_TRANSFER_REFERENCE
      })
    );
    return true;
  }

  /**
   * @notice See `performTransfer`, the spender will be equal to the `owner`. */
  function transferWithRef(address to, uint256 amount, string calldata ref)
      external returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: msg.sender,
        to: to,
        amount: amount,
        ref: ref
      })
    );
    return true;
  }

  function allowance(address owner, address spender)
      public view override returns(uint256) {
    LibFastToken.Data storage s = LibFastToken.data();
    // If the allowance being queried is owned by the reserve, and `spender` is
    // an Issuer member, `spender` owns the full balance of `owner`. If they are
    // not an Issuer member then their allowance is zero. Otherwise, the regular given
    // allowance for `spender` over `owner` applies.
    if (owner == address(0))
      return IHasMembers(LibFast.data().issuer).isMember(spender)
        ? s.balances[owner]
        : 0;
    else
      return s.allowances[owner][spender];
  }

  /**
   * @notice This method directly calls `performApproval`, setting its `from` paramter to the sender of
   * the transaction.
   * @param spender is the address to allow spending from the caller's wallet.
   * @param amount is how much to **increase** the allowance.
   */
  function approve(address spender, uint256 amount)
      external override returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performApproval(msg.sender, spender, amount);
    return true;
  }

  /**
   * @notice This method directly calls `performDisapproval`, setting its `from` parameter to the sender of
   * the transaction.
   * @param spender is the address to disallow spending from the caller's wallet.
   * @param amount is how much to **decrease** the allowance.
   */
  function disapprove(address spender, uint256 amount)
      external
      onlyMember(msg.sender)
      returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performDisapproval(msg.sender, spender, amount);
    return true;
  }

  /// @notice See `performTransfer`, the `ref` will be defaulted.
  function transferFrom(address from, address to, uint256 amount)
      external override returns(bool) {
    transferFromWithRef(from, to, amount, LibFastToken.DEFAULT_TRANSFER_REFERENCE);
    return true;
  }

  /// @notice See `performTransfer`.
  function transferFromWithRef(address from, address to, uint256 amount, string memory ref)
      public returns(bool) {
    // Make sure the call is performed externally so that we can mock.
    this.performTransfer(
      TransferArgs({
        spender: msg.sender,
        from: from,
        to: to,
        amount: amount,
        ref: ref
      })
    );
    return true;
  }

  // Allowances query operations.

  function givenAllowanceCount(address owner)
      external view returns(uint256) {
    return LibFastToken.data().allowancesByOwner[owner].values.length;
  }

  function paginateAllowancesByOwner(address owner, uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastToken.data().allowancesByOwner[owner].values,
      index,
      perPage
    );
  }

  function receivedAllowanceCount(address spender)
      external view returns(uint256) {
    return LibFastToken.data().allowancesBySpender[spender].values.length;
  }

  function paginateAllowancesBySpender(address spender, uint256 index, uint256 perPage)
      external view returns(address[] memory, uint256) {
    return LibPaginate.addresses(
      LibFastToken.data().allowancesBySpender[spender].values,
      index,
      perPage
    );
  }

  // These functions would be internal / private if we weren't using the diamond pattern.
  // Instead, they're `onlyDiamondFacet` - eg can only be called by facets of the current
  // FAST.

  struct TransferArgs {
    address spender;
    address from;
    address to;
    uint256 amount;
    string ref;
  }

  /**
   * @notice This is the internal method that gets called whenever a transfer is initiated. Both `transfer`,
   * `transferWithRef`, and their variants internally call this function.
   *
   * Business logic:
   * - Modifiers:
   *   - Only facets of the current diamond should be able to call this.
   * - Requires that transfers are enabled for this FAST.
   * - Requires that `from` and `to` are different addresses.
   * - Requires that `from` membership is active in the marketplace.
   * - If `from` is not the reserve, requires that `from` is a valid token holder.
   * - If `from` is the reserve, requires that the message sender is an issuer member.
   * - Requires that `to` is a valid token holder.
   * - Requires that the amount is a positive value.
   * - If the transfer is an allowance - e.g. the `spender` is not the same as the `from` address,
   *   - The allowance given by the `from` address to the `spender` covers for the `amount`.
   *     - If we are **not** transfering **from** the reserve,
   *       - Decreases the allowance given by `from` to `spender`.
   *         - If the new allowance reaches zero,
   *           - Stop tracking the allowance in the allowance lookup tables for both spending and receiving directions.
   * - Decreases the balance of the `owner` address.
   * - Increases the balance of the `to` address by `amount`.
   * - If we are **not** transfering **from** the reserve,
   *   - Requires that there are enough transfer credits to cover for `amount`.
   *   - Decreases the transfer credits by `amount`.
   * - If the `to` address is the reserve,
   *   - Decreases the total supply by `amount`.
   *   - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Else, if the `from` address is the reserve,
   *   - Increases the total supply by `amount`.
   *   - Calls `FastFrontendFacet.emitDetailsChanged`.
   * - Calls `FastHistoryFacet.transfered`.
   * - Emits a `Transfer(from, to, amount)` event.
   */
  function performTransfer(TransferArgs calldata p)
      external onlyDiamondFacet {
    // TODO: Make this function return instead of raising errors.
    // TODO: Make this function run even when a zero amount is passed. It should just emit.

    // Grab a pointer to our top-level storage.
    LibFast.Data storage topData = LibFast.data();

    // Requires that transfers are enabled for this FAST.
    if (FastTopFacet(address(this)).transfersDisabled())
      revert ICustomErrors.RequiresTransfersEnabled();
    // Requires that `from` and `to` are different addresses.
    else if (p.from == p.to)
      revert ICustomErrors.RequiresDifferentSenderAndRecipient(p.from);
    // Requires that `from` membership is active in the marketplace.
    else if (!IHasActiveMembers(topData.marketplace).isMemberActive(p.from))
      revert ICustomErrors.RequiresMarketplaceActiveMember(p.from);
    // If `from` is not the reserve, requires that `from` is a valid token holder.
    else if (p.from != address(0) && !canHoldTokens(p.from))
      revert ICustomErrors.RequiresValidTokenHolder(p.from);
    // If `from` is the reserve, requires that the spender (usually the original message sender) is an issuer member.
    else if (p.from == address(0) && !IHasMembers(topData.issuer).isMember(p.spender))
      revert ICustomErrors.RequiresIssuerMembership(p.spender);
    // Requires that `to` is a valid token holder.
    else if (p.to != address(0) && !canHoldTokens(p.to))
      revert ICustomErrors.RequiresValidTokenHolder(p.to);
    // Requires that the amount is a positive value.
    else if (p.amount == 0)
      revert ICustomErrors.UnsupportedOperation();

    // Grab a pointer to our token storage.
    LibFastToken.Data storage tokenData = LibFastToken.data();

    // If this is an allowance transfer and if the `from` account is not the reserve...
    if (p.spender != p.from && p.from != address(0)) {
      // Decrease allowance.
      uint256 newAllowance = tokenData.allowances[p.from][p.spender] -= p.amount;
      // If the allowance reached zero, we want to remove that allowance from
      // the various other places where we keep track of it.
      if (newAllowance == 0) {
        tokenData.allowancesByOwner[p.from].remove(p.spender, true);
        tokenData.allowancesBySpender[p.spender].remove(p.from, true);
      }
    }

    // Keep track of the balances - `from` spends (decrease), `to` receives (increase).
    uint256 fromBalance = (tokenData.balances[p.from] -= p.amount);
    uint256 toBalance = (tokenData.balances[p.to] += p.amount);

    // Keep track of who has what FAST.
    MarketplaceTokenHoldersFacet(topData.marketplace).fastBalanceChanged(p.from, fromBalance);
    MarketplaceTokenHoldersFacet(topData.marketplace).fastBalanceChanged(p.to, toBalance);

    // Keep track of who holds this token.
    balanceChanged(p.from, fromBalance);
    balanceChanged(p.to, toBalance);

    // If the funds are going to the ZERO address, decrease total supply.
    if (p.to == address(0)) {
      tokenData.totalSupply -= p.amount;
      // If funds at address zero changed, we can emit a top-level details change event.
      FastFrontendFacet(address(this)).emitDetailsChanged();
    }
    // If the funds are moving from the zero address, increase total supply.
    else if (p.from == address(0)) {
      tokenData.totalSupply += p.amount;
      // If funds at address zero changed, we can emit a top-level details change event.
      FastFrontendFacet(address(this)).emitDetailsChanged();
    }

    // Keep track of the transfer in the history facet.
    FastHistoryFacet(address(this)).transfered(p.spender, p.from, p.to, p.amount, p.ref);

    // Emit!
    emit Transfer(p.from, p.to, p.amount);
  }

  /**
   * @notice Increases the allowance given by `from` to `spender` by `amount`.
   * Note that this function should run and emit even if the amount passed is zero.
   * Business logic:
   * - Modifiers:
   *   - Only facets of the current diamond should be able to call this.
   *   - Requires that `onlyTokenHolder` passes for the `from` address.
   * - Requires that the `amount` is positive number.
   * - Increases the allowance given by `from` to `spender` by `amount`.
   * - Update the allowance lookup tables in both directions.
   * - Emits an `Approval(from, spender, amount)`.
   * @param from is the wallet from which to give the allowance.
   * @param spender is the receiver of the allowance.
   * @param amount is how much to **increase** the current allowance by.
   * 
   * Note: This function runs when amount is zero, and will emit.
   */
  function performApproval(address from, address spender, uint256 amount)
      external
      onlyDiamondFacet {
    // Require that the `from` address can hold tokens.
    if (!canHoldTokens(from))
      revert ICustomErrors.RequiresValidTokenHolder(from);
    // Require that the `from` address cannot be the reserve.
    else if (from == address(0))
      revert ICustomErrors.UnsupportedOperation();

    if (amount > 0) {
      LibFastToken.Data storage s = LibFastToken.data();
      // Note that we are not exactly following ERC20 here - we don't want to **set** the allowance to `amount`
      // to mitigate a possible attack.
      // See https://docs.google.com/document/d/1YLPtQxZu1UAvO9cZ1O2RPXBbT0mooh4DYKjA_jp-RLM/edit#heading=h.gmr6zdg47087.
      s.allowances[from][spender] += amount;
    // Keep track of given and received allowances.
      s.allowancesByOwner[from].add(spender, true);
      s.allowancesBySpender[spender].add(from, true);
    }

    // Emit!
    emit Approval(from, spender, amount);
  }

  /**
   * @notice Decreases allowance given by `from` to `spender` by `amount`.
   *
   * Business logic:
   * - Modifiers:
   *   - Only facets of the current diamond should be able to call this.
   * - The allowance given by `from` to `spender` is decreased by `amount`.
   * - Whether the allowance reached zero, stop tracking it by owner and by spender.
   * - Emit a `Disapproval(from, spender, amount)` event.
   * 
   * Note: This function runs when amount is zero, and will emit.
   */
  function performDisapproval(address from, address spender, uint256 amount)
      external
      onlyDiamondFacet {
    if (amount != 0) {
      LibFastToken.Data storage s = LibFastToken.data();

      // Remove allowance.
      s.allowances[from][spender] -= amount;

      // Whenever the allowance reaches zero, stop tracking it by owner and spender.
      if (s.allowances[from][spender] == 0) {
        s.allowancesByOwner[from].remove(spender, true);
        s.allowancesBySpender[spender].remove(from, true);
      }
    }

    // Emit!
    emit Disapproval(from, spender, amount);
  }

  // WARNING: This method contains two loops. We know that this should never
  // happen in solidity. However:
  // - In the context of our private chain, gas is cheap.
  // - It can only be called by a governor.
  function beforeRemovingMember(address member)
      external onlyDiamondFacet() {
    if (balanceOf(member) != 0)
      revert ICustomErrors.RequiresPositiveBalance(member);

    LibFastToken.Data storage s = LibFastToken.data();

    // Remove all given allowances.
    {
      address[] storage gaData = s.allowancesByOwner[member].values;
      while (gaData.length > 0) {
        // Make sure the call is performed externally so that we can mock.
        address spender = gaData[0];
        this.performDisapproval(member, spender, s.allowances[member][spender]);
      }
    }

    // Remove all received allowances.
    {
      address[] storage raData = s.allowancesBySpender[member].values;
      while (raData.length > 0) {
        // Make sure the call is performed externally so that we can mock.
        address owner = raData[0];
        this.performDisapproval(owner, member, s.allowances[owner][member]);
      }
    }
  }

  function holders()
      external view
      returns(address[] memory) {
    LibFastToken.Data storage s = LibFastToken.data();
    return s.tokenHolders.values;
  }

  function balanceChanged(address holder, uint256 balance)
      private {
    // Return early if this is the zero address.
    if (holder == address(0))
      return;

    LibFastToken.Data storage s = LibFastToken.data();

    // If this is a positive balance and it doesn't already exist in the set, add address.
    if (balance > 0 && !s.tokenHolders.contains(holder))
      s.tokenHolders.add(holder, false);
    // If the balance is 0 and it exists in the set, remove it.
    else if (balance == 0 && s.tokenHolders.contains(holder))
      s.tokenHolders.remove(holder, false);
  }

  // Private and helper methods.

  /**
   * @notice Ensures that the given address is a member of the current FAST or the Zero Address.
   *
   * Business logic:
  *    - If the fast is semi-public,
  *      - We require that candidate is a member of the Marketplace contract and is active in it.
   *  - Otherwise,
   *    - Require that the candidate is a member of the FAST.
   * @param candidate The address to check.
   * @return A boolean set to `true` if `candidate` can hold tokens, `false` otherwise.
   */
  function canHoldTokens(address candidate)
      private view returns(bool) {
    // If the fast is semi public, any member of the marketplace can hold tokens.
    if (IFast(address(this)).isSemiPublic()) {
      address m = LibFast.data().marketplace;
      return IHasMembers(m).isMember(candidate) && IHasActiveMembers(m).isMemberActive(candidate);
    }
    // FAST is private, only members of the fast can hold tokens.
    else
      return IHasMembers(address(this)).isMember(candidate);
  }
}
","keccak256":"0x687dd546afd69d6820dcae003c1694844b31e4d1695c5a7567cafc9b590fec2f","license":"MIT"},"contracts/fast/FastTopFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../lib/LibConstants.sol';
import '../lib/LibHelpers.sol';
import './lib/AFastFacet.sol';
import './lib/LibFast.sol';
import './lib/IFastEvents.sol';
import './FastFrontendFacet.sol';


contract FastTopFacet is AFastFacet {
  // Getters and setters for global flags.

  /**
   * @notice Get the Issuer address.
   * @return Address of Issuer.
   */
  function issuerAddress()
      external view returns(address) {
    return LibFast.data().issuer;
  }

  /**
   * @notice Get the Marketplace address.
   * @return address Address of Marketplace.
   */
  function marketplaceAddress()
      external view returns(address) {
    return LibFast.data().marketplace;
  }

  /**
   * @notice Is this FAST a semi public FAST?
   * @return bool Yes/no semi public.
   */
  function isSemiPublic()
      external view returns(bool) {
    return LibFast.data().isSemiPublic;
  }

  /**
   * @notice Is this FAST a fixed supply FAST?
   * @return bool Yes/no fixed supply.
   */
  function hasFixedSupply()
      external view returns(bool) {
    return LibFast.data().hasFixedSupply;
  }

  /**
   * @notice Are transfers enabled across this FAST?
   * @return `true` if transfers are disabled, `false` if transfers are enabled.
   */
  function transfersDisabled()
      external view returns(bool) {
    return LibFast.data().transfersDisabled;
  }

  // Setters for global flags.

  /**
   * @notice Allows to switch from a private scheme to a semi-public scheme,
   *  but not the other way around.
   * @param flag Set the semi public flag to true/false.
   */
  function setIsSemiPublic(bool flag)
      external
      onlyIssuerMember {
    // Someone is trying to toggle back to private?... No can do!
    if (this.isSemiPublic()) {
      revert ICustomErrors.UnsupportedOperation();
    }
    LibFast.data().isSemiPublic = flag;
    // Emit!
    FastFrontendFacet(address(this)).emitDetailsChanged();
  }

  /**
   * @notice Allows an issuer member to enable or disable all transfers within this FAST.
   * @param flag Set the transfer capability to active or not.
   */
  function setTransfersDisabled(bool flag)
      external
      onlyIssuerMember {
    LibFast.Data storage d = LibFast.data();
    // Only make changes and emit if the new flag is different than the old one.
    if (d.transfersDisabled != flag) {
      // Set flag.
      d.transfersDisabled = flag;
      // Emit!
      FastFrontendFacet(address(this)).emitDetailsChanged();
    }
  }
}
","keccak256":"0x28f4712ff765c19a0113044954a839b933b88635a405fd0b3f4be033a826afb9","license":"MIT"},"contracts/fast/lib/AFastFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '../../lib/LibConstants.sol';
import '../../lib/LibHelpers.sol';
import '../../lib/LibAddressSet.sol';
import '../../interfaces/ICustomErrors.sol';
import '../../interfaces/IHasMembers.sol';
import '../../interfaces/IHasGovernors.sol';
import '../../interfaces/IHasActiveMembers.sol';
import '../../interfaces/IERC173.sol';
import '../lib/LibFast.sol';
import './IFastEvents.sol';


/**
 * @title Abstract FAST helper contract.
 * @notice This abstract contract encapsulates modifiers allowing inheriting facets to guard against
 * certain permissions.
 */
abstract contract AFastFacet is IFastEvents {
  using LibAddressSet for LibAddressSet.Data;

  /// Modifiers.

  /// @notice Ensures that a method can only be called by another facet of the same diamond.
  modifier onlyDiamondFacet() {
    if (msg.sender != address(this))
      revert ICustomErrors.InternalMethod();
    _;
  }

  /// @notice Ensures that a method can only be called by the owner of this diamond.
  modifier onlyDiamondOwner() {
    if (msg.sender != IERC173(address(this)).owner())
      revert ICustomErrors.RequiresDiamondOwnership(msg.sender);
    _;
  }

  /// @notice Ensures that a method can only be called by the singleton deployer contract factory.
  modifier onlyDeployer() {
    if (msg.sender != LibConstants.DEPLOYER_CONTRACT)
      revert ICustomErrors.InternalMethod();
    _;
  }

  /**
   * @notice Ensures that the given address is a member of the Marketplace.
   * @param candidate The address to check.
   */
  modifier onlyMarketplaceMember(address candidate) {
    if (!IHasMembers(LibFast.data().marketplace).isMember(candidate))
      revert ICustomErrors.RequiresMarketplaceMembership(candidate);
    _;
  }

  /**
   * @notice Ensures a candidate is an active member of the Marketplace.
   * @param candidate The address to check.
   */
  modifier onlyMarketplaceActiveMember(address candidate) {
    if (!IHasActiveMembers(LibFast.data().marketplace).isMemberActive(candidate))
      revert ICustomErrors.RequiresMarketplaceActiveMember(candidate);
    _;
  }

  /**
   * @notice Ensures that the message sender is a member of the Issuer.
   */
  modifier onlyIssuerMember() {
    if (!IHasMembers(LibFast.data().issuer).isMember(msg.sender))
      revert ICustomErrors.RequiresIssuerMembership(msg.sender);
    _;
  }

  /**
   * @notice Ensures that the given address is a governor of the FAST.
   * @param candidate The address to check.
   */
  modifier onlyGovernor(address candidate) {
    if (!IHasGovernors(address(this)).isGovernor(candidate))
      revert ICustomErrors.RequiresFastGovernorship(candidate);
    _;
  }

  /**
   * @notice Ensures that the given address is a member of the FAST.
   * @param candidate The address to check.
   */
  modifier onlyMember(address candidate) {
    if (!IHasMembers(address(this)).isMember(candidate))
      revert ICustomErrors.RequiresFastMembership(candidate);
    _;
  }

  /**
   * @notice Ensures address `a` and `b` are different.
   * @param a Address a
   * @param b Address b
   */
  modifier differentAddresses(address a, address b) {
    if (a == b)
      revert ICustomErrors.RequiresDifferentSenderAndRecipient(a);
    _;
  }
}
","keccak256":"0x767d0c050370911dfae78ee917742a8a32e14e4688172a8f606ce1e4fb6ec764","license":"MIT"},"contracts/fast/lib/IFast.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;


/// @title The top-level all FAST diamonds must comply to.
interface IFast {
  /**
   * @notice Queries whether the FAST is semi-public or closed.
   * @return A `bool` being `true` if the FAST is semi-public.
   */
  function isSemiPublic() external view returns(bool);

  /**
   * @notice Queries whether the FAST has fixed or continuous supply.
   * @return A `bool` being `true` if the FAST has fixed supply.
   */
  function hasFixedSupply() external view returns(bool);
}
","keccak256":"0xb4c06e0a41be3f4f50b29a5e3d35e3e7a802736fb75a5b06d6f2b4cf4f56acaf","license":"MIT"},"contracts/fast/lib/IFastEvents.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;


/**
 * @title FAST events inventory.
 * @notice An interface allowing to use events within the Diamond pattern without name colisions.
 * @dev The idea is that as several facets can emit the same events, we don't want to have to re-declare
 * the same event several time. This interface is a per-diamond central place for such event declaration.
 */
interface IFastEvents {
  // IHasMembers.

  /**
   * @notice Emited when a member is added to the implementing contract.
   * @param member is the address of the added member.
   */
  event MemberAdded(address indexed member);
  /**
   * @notice Emited when a member is removed to the implementing contract.
   * @param member is the address of the removed member.
   */
  event MemberRemoved(address indexed member);

  // IHasGovernors.

  /**
   * @notice Emited when a governor is added to the implementing contract.
   * @param governor is the address of the added governor.
   */
  event GovernorAdded(address indexed governor);
  /**
   * @notice Emited when a governor is removed to the implementing contract.
   * @param governor is the address of the removed member.
   */
  event GovernorRemoved(address indexed governor);

  // Issuance related events.

  /**
   * @notice Emited whenever an issuance happens in a FAST.
   * @param amount is the amount of tokens that have been minted.
   * @param ref is the reference associated with the minting operation.
   */
  event Minted(uint256 indexed amount, string indexed ref);
  /**
   * @notice Emited whenever an burning happens in a FAST.
   * @param amount is the amount of tokens that have been burnt.
   * @param ref is the reference associated with the burning operation.
   */
  event Burnt(uint256 indexed amount, string indexed ref);

  // ERC20 stuff.

  /// @notice See `ERC20.Transfer`.
  event Transfer(address indexed from, address indexed to, uint256 value);
  /// @notice See `ERC20.Approval`.
  event Approval(address indexed owner, address indexed spender, uint256 value);
  /// @notice See `ERC20.Disapproval`.
  event Disapproval(address indexed owner, address indexed spender, uint256 value);

  // General events.

  /**
   * @notice This is an event that is fired whenever any of some of the FAST parameters
   * change, so that the frontend can react to it and refresh the general header
   * for that fast as well as the baseball cards in the FASTs list.
   * @param transfersDisabled marks whether or not transfers are disabled by an issuer member at FAST level.
   * @param memberCount is the number of members in the FAST.
   * @param governorCount is the number of governors in the FAST.
   * @param totalSupply is the amount of tokens in circulation in the FAST.
   * @param reserveBalance is the balance of the zero-address (aka reserve) for the FAST.
   * @param ethBalance is the amount of Eth locked in the FAST.
   */
  event DetailsChanged(
    bool transfersDisabled,
    uint256 memberCount,
    uint256 governorCount,
    uint256 totalSupply,
    uint256 reserveBalance,
    uint256 ethBalance
  );
}
","keccak256":"0x3fefca074ca023ba3d751ed60c7fd5440445c350547a904678aec0c03af839af","license":"MIT"},"contracts/fast/lib/LibFast.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;


/**
 * @notice Top-level shared functionality for FAST diamonds.
 * @dev Note that if you feel like a method should be created inside this library, you might want to really consider
 * whether or not it is the right place for it. Any facet using a method from internal libraries see their bytecode
 * size increase, kind of defeating the benefits of using facets in the first place. So please keep it reasonable. 
 */
library LibFast {
  /// @notice The current version of the storage.
  uint16 internal constant STORAGE_VERSION = 1;
  /// @notice This is keccak256('Fast.storage'):
  bytes32 internal constant STORAGE_SLOT = 0x80c187ea6f955fd624c41fb7a18011cc87d98c6f4c06d897b59142f65707e705;

  // Data structures.

  /**
   * @notice The top-level data structure required for operating any given FAST diamond.
   * @dev The `version` field is used to ensure that storage is at a known version during upgrades.
   */
  struct Data {
    /// @notice The latest intializer version that was called.
    uint16 version;
    /// @notice The internal pointer to the Issuer contract.
    address issuer;
    /// @notice The internal pointer to the Marketplace contract.
    address marketplace;
    /// @notice We have to track whether the token facet provides continuous minting or fixed supply.
    bool hasFixedSupply;
    /// @notice Whether or not this FAST requires to be a member to hold tokens.
    bool isSemiPublic;
    /// @notice A flag which when toggled to `true` disables all transfers across this FAST.
    bool transfersDisabled;
  }

  /**
   * @notice Returns the top-level storage for the calling FAST.
   * @return s a struct pointer for top-level FAST data storage.
   */
  function data()
      internal pure returns(Data storage s) {
    assembly {s.slot := STORAGE_SLOT}
  }
}
