pragma solidity 0.8.10;

import '../../lib/LibAddressSet.sol';


library LibMarketplaceAccess {
  // The current version of the storage.
  uint16 internal constant STORAGE_VERSION = 1;
  // This is keccak256('Marketplace.storage.Access'):
